<?php// 用户代理作为对象属性class MobileSniffer {	private $userAgent;	private $device;	private $browser;	private $deviceLength;	private $browserLength;	public function __construct() {		$this->userAgent = $_SERVER['HTTP_USER_AGENT'];		$this->userAgent = strtolower($this->userAgent);		$this->device = ['iphone', 'ipad', 'android', 'silk', 'blackberry', 'touch', 'windows'];		$this->browser = ['firefox', 'chrome', 'opera', 'msie', 'safari', 'blackberry', 'trident'];		$this->deviceLength = count($this->device);		$this->browserLength = count($this->browser);	}	public function findDevice() {		for ($uaSniff = 0; $uaSniff < $this->deviceLength; $uaSniff++) {			$useAgent =  $this->userAgent;			$device = $this->device[$uaSniff];			if (strstr($useAgent, $device)) {				return $device;			}		}		return false;	}	public function findBrowser() {		for ($uaSniff = 0; $uaSniff < $this->browserLength; $uaSniff++) {			$useAgent =  $this->userAgent;			$browser = $this->browser[$uaSniff];			if (strstr($useAgent, $browser)) {				return $browser;			}		}		return false;	}}